package Javapractice;

public class Pattern6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   int i ;
   int j;
   int k;
   int l;
   int m;
   for(i=0; i<=5;i++) {
	   for(j=4;j>=i;j--) {
		   System.out.print("*");
	   }
	   for(l=0;l<=i; l++) {
		   System.out.print("*");
	   }
	   
	   
	   
	   
	   System.out.println("");
   }
	}

}
